package basics;

public class Basics1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0,j=0,k=0;
		System.out.println(i++);
		System.out.println(i);
		System.out.println(++i);
		System.out.println(i);
		System.out.println(j--);
		System.out.println(j);
		System.out.println(--j);
		System.out.println(j);
		int x=k++;
		System.out.println(x+"   "+k++);
		System.out.println(k--);

	}

}
